
public class SubString23 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		String sentences="java ile herþey çok guzel    ";
		
		System.out.println(sentences.substring(4));
		System.out.println(sentences.substring(1, 4));
		System.out.println(sentences.subSequence(2, 9));
		System.out.println(sentences.substring(1, 1));
		System.out.println(sentences.substring(1, 2));
		
		System.out.println(sentences.trim());
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
