package Day10_Switccase;

public class StringMnupulastyonCharat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    //  charAt metodu istediðimiz indeksteki karakter veriri 
		
		String kurs= "marmarayýldýz";
		System.out.println(kurs.charAt(6));  // indeks 0 dan baslar 
		System.out.println(kurs.charAt(0));  // ilk harfi verir 
		System.out.println(kurs.charAt(12)); // hata verir
		// son indexs uzunluk eksi 1 
		System.out.println(kurs.toLowerCase());
		System.out.println(kurs.toUpperCase());
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
